mapper.select:
select a.*,b.fileid, b.belongid,b.path, b.fileidname, b.filename, b.filetype, b.filesize, b.status status_c, b.createby createby_c1, b.createat createat_c1 FROM( SELECT t1.id,columns, classification, title, viewImg, viewCount, likeCount, status, recommended, t1.createby, t1.createat, updateTime, t2.id id_c, t2.ladelname, t2.parentId, t2.hierarchy, t2.craeatby craeatby_c, t2.createat createat_c FROM t_cms_articleld AS t1 ,t_cms_atticleld_category as t2 WHERE t1.columns=#{columns}
and t1.classification=#{classification}
and t1.classification=t2.id and t1.status='Y' ORDER BY t1.createat desc ) a LEFT JOIN t_file_path b ON a.id = b.belongid

mapper.update:
update set

viewCount=#{viewCount}


likeCount=#{likeCount}

where id=#{id}
