mapper.select:
SELECT A.*, B.path, B.fileidname, B.filename, B.filetype FROM (
select K.*, H.user_name user_name_u, H.NAME name_u, H.Sex, H.Phone Phone_u, H.email email_u, H.qq qq_u, H.address_province, H.address_city, H.address_area, H.address_street, H.headimgurl, H.Create_at Create_at_u, H.Update_time Update_time_u, H.LastSign_time LastSign_time_u
from ( SELECT I.*, J.user_id user_id_a, J.name, J.phone, J.province, J.city, J.area, J.street FROM( SELECT t1.order_id, t1.order_number, t1.user_id, t1.total_amount, t1.paid_amount, t1.postage_amount, t1.discount_amount, t1.receive_id, t1.send_time, t1.logistics_state, t1.invoice_tag, t1.del_state, t1.payment_id, t1.payment_seq, t1.pay_state, t1. COMMENT, t1.create_time, t2.order_details_id, t2.order_id order_id_d, t2.order_number order_number_d, t2.price_id, t2.goods_id, t2.unit_name, t2.goods_name, t2.unit_price, t2.num, t2.details_amount, t2.image FROM t_order t1 LEFT JOIN t_order_details t2 ON t1.order_number = t2.order_number
t1.user_id = #{user_id}
AND t1.order_number=#{order_number}
AND t1.del_state=#{del_state}
AND t1.pay_state=#{pay_state}
AND t1.logistics_state=#{logistics_state}
) I LEFT JOIN t_order_address J ON I.receive_id=J.id
J.name LIKE CONCAT('%',#{orderAddress.name},'%')
)K LEFT JOIN t_sys_user_info H ON K.user_id=H.user_name ) A LEFT JOIN t_file_path B ON A.image = B.fileid ORDER BY A.create_time DESC

mapper.update:
 update t_order
  
  
  pay_state=#{pay_state},
  
  
  del_state=#{del_state},
  
  
  logistics_state=#{logistics_state}
  
	
	where order_number=#{order_number}
